sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    // Clear Client State
    api.setState("now_serving", "");
    api.setState("first_in_queue", "");
    // Assign new values
    api.setState("now_serving", api.data.look_up_records_1.results[0].sys_id.value);
    api.setState("first_in_queue", api.data.look_up_records_2.results[0].sys_id.value);
}
