sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    function transform() {
        api.data.registration.execute({
            first: api.state.first,
            last: api.state.last,
            email: api.state.email,
            org: api.state.org,
            location: loc_param
        });

    }
    var loc_param = api.context.props.location;

    switch (loc_param) {
        case "hague":
            loc_param = "The Hague";
            break;
        case "lv":
            loc_param = "Las Vegas";
            break;
        case "new_york":
            loc_param = "New York";
            break;
        case "sydney":
            loc_param = "Sydney";
            break;
    }

    var sysID = transform();
    console.log(sysID);
    if (sysID != '') {
        api.setState.sysID;
    }
}
