sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    function transform() {
        api.data.registration.execute({
            first: api.state.first,
            last: api.state.last,
            email: api.state.email,
            org: api.state.org,
            location: api.state.location
        });

    }
    var sysID = transform();
    console.log(sysID);
    if (sysID != ''){
        api.setState.sysID;
    }
}
